import React, { useRef, useState, useEffect } from 'react'
import Input from '../component/Input'
import validate from 'react-joi-validation'
import Joi from 'joi-browser'
function Login(props) {
  const usernameref = useRef()
  const passwordref = useRef()
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [userNameError, setUserNameError] = useState('')
  const [passworderror, setPasswordError] = useState('')
  const [ac, setac] = useState({ username: '', password: '' })

  var schema = Joi.object().keys({
    username: Joi.string().required(),
    password: Joi.string().min(8).required(),
  })

  const handleSubmit = (e) => {
    e.preventDefault()
    //const result = Joi.validate(ac, schema)
    // console.log(result.error.details)

    /*  try {
      const value = Joi.validate(
        username,

        schema
      )
      console.log(value)
    } catch (err) {} */
  }

  return (
    <form onSubmit={handleSubmit}>
      <Input
        name="username"
        ref={usernameref}
        onChange={(e) => {
          const result = Joi.validate(username, schema)
          setUsername(e.target.value)
          if (result.error !== '') setUserNameError('')
          else setUserNameError('Username is required')
        }}
        error={userNameError}
      ></Input>
      <Input
        name="password"
        ref={passwordref}
        onChange={(e) => {
          setPassword(e.target.value)
          if (e.target.value !== '') setPasswordError('')
          else setPasswordError('Password is required')
        }}
        error={passworderror}
      ></Input>

      <button type="submit" className="btn btn-primary">
        Login
      </button>
    </form>
  )
}

export default Login
