import Input from '../../component/Input'

function Add(props) {
  const [title, setTitle] = useState('')
  const titleRef = useRef('')

  return (
    <>
      <Input
        lable="title"
        name="title"
        type="text"
        ref={titleRef}
        onChange={() => console.log(titleRef.current)}
      ></Input>
    </>
  )
}

export default Add
