import React, { useRef, useState, useEffect } from 'react'
import Input from '../component/Input'
import validate from 'react-joi-validation'
import Joi from 'joi'
function Login(props) {
  const usernameref = useRef()
  const passwordref = useRef()
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [userNameError, setUserNameError] = useState('')
  const [passworderror, setPasswordError] = useState('')

  var schema = Joi.object().keys({
    username1: Joi.string().required(),
    password1: Joi.string().min(8).required(),
  })

  const handleSubmit = async (e) => {
    e.preventDefault()

    try {
      const value = await schema.validateAsync({
        username1: username,
        password1: password,
      })
      console.log(value)
    } catch (err) {}

    //validate(Login, schema)
    //const res = Joi.valid(username, schema)
    // console.log(res)
    /* if (username.trim() === '') {
      setUserNameError('Username is required')
    } else setUserNameError('')
    if (password.trim() === '') {
      setPasswordError('Password is required')
    } else setPasswordError('') */
  }

  return (
    <form onSubmit={handleSubmit}>
      <Input
        name="username"
        ref={usernameref}
        onChange={(e) => {
          setUsername(e.target.value)
          if (e.target.value !== '') setUserNameError('')
          else setUserNameError('Username is required')
        }}
        error={userNameError}
      ></Input>
      <Input
        name="password"
        ref={passwordref}
        onChange={(e) => {
          setPassword(e.target.value)
          if (e.target.value !== '') setPasswordError('')
          else setPasswordError('Password is required')
        }}
        error={passworderror}
      ></Input>

      <button type="submit" className="btn btn-primary">
        Login
      </button>
    </form>
  )
}

export default Login
