import React, { useRef, useState, useEffect } from 'react'
import Joi from 'joi'

function Form(props) {
  const [errors, setErrors] = useState('')

  var schema = Joi.object().keys({
    username1: Joi.string().required().label('UserName'),
    password1: Joi.string().required().label('Password'),
  })

  const handleSubmit = (e) => {
    e.preventDefault()
    try {
      const { error } = schema.validate({
        username1: props.data['username'],
        password1: props.data['password'],
      })
      error ? setErrors(error.details[0].message) : setErrors('')
      setErrors(error.details[0].message)
    } catch (err) {}
  }

  return (
    <form onSubmit={handleSubmit}>
      {errors && (
        <div className="alert alert-danger" role="alert">
          {errors}
        </div>
      )}

      {props.children}
    </form>
  )
}

export default Form
