import React, { useState } from 'react'
import Input from '../../component/Input'
import Form from '../../component/Form'
import Select from '../../component/Select'
import { getGenres } from '../../services/fakeGenreService'
import { getMovies, saveMovie } from '../../services/fakeMoviesService'
import { useLocation, useNavigate, useParams } from 'react-router-dom'

import Button from '../../component/Button'
import Joi from 'joi'

function New(props) {
  const [errors, setErrors] = useState('')
  const [title, setTitle] = useState('')
  const [numberInStock, setNumberInStock] = useState('')
  const [rate, setRate] = useState('')
  const [genre, setGenre] = useState('')
  let params = useParams()
  console.log('====================================')
  console.log(params)
  console.log('====================================')
  var schema = Joi.object().keys({
    Title: Joi.string().required(),
    NumberInStock: Joi.number().min(0).max(10).required(),
    Genre: Joi.required(),
    Rate: Joi.number().required(),
  })

  const handleSubmit = (e) => {
    const movie = {
      title: '',
      genreId: '',
      numberInStock: '',
      dailyRentalRate: '',
    }
    e.preventDefault()

    // try {
    const { error } = schema.validate({
      Title: title,
      NumberInStock: numberInStock,
      Rate: rate,
      Genre: genre,
    })
    error ? setErrors(error.details[0].message) : setErrors('')
    if (error) setErrors(error.details[0].message)

    movie.title = title
    movie.genreId = genre
    movie.numberInStock = numberInStock
    movie.dailyRentalRate = rate

    const id = saveMovie(movie)
    console.log(id)
    // } catch (error) {}
  }
  return (
    <Form onSubmit={handleSubmit} errors={errors}>
      <Input
        lable="Title"
        name="Title"
        onChange={(e) => setTitle(e.target.value)}
      ></Input>
      <Select
        lable="Genres"
        id="genres"
        data={getGenres()}
        onChange={(e) => {
          console.log('====================================')
          console.log(e.target.value)
          console.log('====================================')
          setGenre(e.target.value)
        }}
      ></Select>
      <Input
        lable="Number In Stock"
        name="NumberInStock"
        onChange={(e) => setNumberInStock(e.target.value)}
      ></Input>
      <Input
        lable="Rate"
        name="Rate"
        onChange={(e) => setRate(e.target.value)}
      ></Input>
      <Button lable="Save"></Button>
    </Form>
  )
}

export default New
