import axios from 'axios'
import React, { useEffect, useState } from 'react'
import Table from '../../component/Table'
import paginate from '../../utils/paginate'
import Pagination from '../../component/Pagination'
import _ from 'lodash'
import Button from '../../component/Button'
import { Update } from 'react-lodash'

/* {
    "userId": 1,
    "id": 1,
    "title": "sunt aut facere repellat provident occaecati excepturi optio reprehenderit",
    "body": "quia et suscipit\nsuscipit recusandae consequuntur expedita et cum\nreprehenderit molestiae ut ut quas totam\nnostrum rerum est autem sunt rem eveniet architecto"
  }, */

function List(props) {
  const [data, setData] = useState()
  const [currentPage, setCurrentPage] = useState(1)
  const [pageSize, setPageSize] = useState(5)

  const updateItem = async () => {
    const { t1 } = { id: 1, title: 'Somayeh', body: 'e3' }

    const data = await axios.put(
      `https://jsonplaceholder.typicode.com/posts/` + t1.id,
      t1
    )
    console.log('====================================')
    console.log(data)
    console.log('====================================')
  }
  const handelUpdate = () => {
    updateItem()
  }
  const columns = [
    {
      path: 'id',
      lable: 'id',
    },
    {
      path: 'title',
      lable: 'Title',
    },
    /* { path: 'body', lable: 'Body' },
     */
    {
      path: 'Update',
      content: <Button onClick={handelUpdate} lable="Update"></Button>,
    },
    {
      path: 'Delete',
      content: (
        <Button
          onClick={() => console.log('Delete')}
          className="btn-danger"
          lable="Delete"
        ></Button>
      ),
    },
  ]
  const getData = async () => {
    const { status, data } = await axios.get(
      'https://jsonplaceholder.typicode.com/posts'
    )
    setData(data)
  }

  useEffect(() => {
    getData()
  }, [])
  const Save = async () => {
    const { data: post } = await axios.post(
      'https://jsonplaceholder.typicode.com/posts',
      { title: 'a', body: 'd' }
    )
    const p = [post, ...data]
    setData(p)
  }
  const handelClick = () => {
    //setPost({ title: titleRef.current.value, body: bodyRef.current.value })
    Save()
  }
  return (
    <>
      <Button lable="Save" onClick={handelClick}></Button>

      <Table columns={columns} data={data}></Table>
    </>
  )
}
{
  /* <a className="btn btn-primary" href="Add" role="button">
        New
      </a> */
}
export default List
