import React, { useRef, useState, useEffect } from 'react'
import Button from '../component/Button'
import Form from '../component/Form'
import Input from '../component/Input'
import Joi from 'joi'

function Register(props) {
  const [errors, setErrors] = useState('')
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')

  var schema = Joi.object().keys({
    //usernameschema: Joi.required().email.label('UserName'),
    passwordschema: Joi.string().required().label('Password'),
    usernameschema: Joi.string().email({
      minDomainSegments: 2,
      tlds: { allow: ['com', 'net'] },
    }),
  })
  /* var schema = Joi.object().keys({
    usernameschema: Joi.string().email().required().label('UserName'),
    passwordschema: Joi.string().required().min(8).label('Password'),
    name: Joi.string().required().label('Name'),
  }) */
  const handleSubmit = (e) => {
    console.log('handleSubmit')
    e.preventDefault()
    try {
      const { error } = schema.validate({
        username1: username,
        password1: password,
      })
      error ? setErrors(error.details[0].message) : setErrors('')
      setErrors(error.details[0].message)
    } catch (err) {}
  }
  return (
    <div>
      <Form onSubmit={handleSubmit} errors={errors}>
        <Input name="UserName" type="email"></Input>
        <Input name="password" type="password"></Input>
        <Input name="Name" type="text"></Input>
        <Button className=" btn-primary" lable="Register"></Button>
      </Form>
    </div>
  )
}

export default Register
