import React, { useRef, useState, useEffect } from 'react'
import Input from '../component/Input'
import Joi from 'joi'
function Login(props) {
  const usernameref = useRef()
  const passwordref = useRef()
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [userNameError, setUserNameError] = useState('')
  const [passworderror, setPasswordError] = useState('')
  const Joi = require('joi')

  const schema = Joi.object({
    username1: Joi.string().required(),
    password1: joi.string().required(),
  })
  const handleSubmit = (e) => {
    e.preventDefault()

    const result = schema.validateAsync({ username1: 'aaa', username1: 'fff' })
    console.log('====================================')
    console.log(result)
    console.log('====================================')
    /* if (username.trim() === '') {
      setUserNameError('Username is required')
    } else setUserNameError('')
    if (password.trim() === '') {
      setPasswordError('Password is required')
    } else setPasswordError('') */
  }

  return (
    <form onSubmit={handleSubmit}>
      <Input
        name="username"
        ref={usernameref}
        onChange={(e) => {
          setUsername(e.target.value)
          if (e.target.value !== '') setUserNameError('')
          else setUserNameError('Username is required')
        }}
        error={userNameError}
      ></Input>
      <Input
        name="password"
        ref={passwordref}
        onChange={(e) => {
          setPassword(e.target.value)
          if (e.target.value !== '') setPasswordError('')
          else setPasswordError('Password is required')
        }}
        error={passworderror}
      ></Input>

      <button type="submit" className="btn btn-primary">
        Login
      </button>
    </form>
  )
}

export default Login
