import React, { useRef, useState, useEffect } from 'react'
import Input from '../component/Input'
import validate from 'react-joi-validation'
import Joi from 'joi'
function Login(props) {
  const usernameref = useRef()
  const passwordref = useRef()
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [userNameError, setUserNameError] = useState('')
  const [passworderror, setPasswordError] = useState('')
  const [errors, setErrors] = useState('')

  var schema = Joi.object().keys({
    username1: Joi.string().required().label('UserName'),
    password1: Joi.string().min(8).required().label('Password'),
  })

  const handleSubmit = (e) => {
    e.preventDefault()

    try {
      const { error } = schema.validate({
        username1: username,
        password1: password,
      })
      console.log(error.details)
      for (let item of error.details) {
        console.log(item.path)

        setErrors(item.message)
      }
    } catch (err) {}

    //validate(Login, schema)
    //const res = Joi.valid(username, schema)
    // console.log(res)
    /* if (username.trim() === '') {
      setUserNameError('Username is required')
    } else setUserNameError('')
    if (password.trim() === '') {
      setPasswordError('Password is required')
    } else setPasswordError('') */
  }
  validationPro = () => {
    const { error } = schema.validate({ username1: username })
    // return error ? err.details[0].message : null
    console.log(error.details[0].message)
  }
  return (
    <form onSubmit={handleSubmit}>
      {errors && (
        <div className="alert alert-danger" role="alert">
          {errors}
        </div>
      )}
      <Input name="username" ref={usernameref} onChange={validationPro}></Input>
      <Input
        name="password"
        ref={passwordref}
        onChange={(e) => {
          setPassword(e.target.value)
          /* if (e.target.value !== '') setPasswordError('')
          else setPasswordError('Password is required') */
        }}
      ></Input>

      <button type="submit" className="btn btn-primary">
        Login
      </button>
    </form>
  )
}

export default Login
